(()=>{"use strict";document.getElementById("searchBtn").addEventListener("click",(async function(){const e=document.getElementById("searchValue").value,t=document.getElementById("weatherType"),n=document.getElementById("cityName"),d=document.getElementById("temperature"),i=document.getElementById("feel"),m=document.getElementById("wind"),a=document.getElementById("humidity"),c=document.getElementById("mainContent");let o=273.15;try{const l=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${e}&appid=c1b647a23c9bdda2769d0c7cde5b0042`),r=await l.json();c.style.display="flex",t.innerHTML=r.weather[0].description,n.innerText=r.name,d.innerText=`${(r.main.temp-o).toFixed(2)} °C`,i.innerText=`Feels like: ${(r.main.feels_like-o).toFixed(2)} °C`,m.innerText=`Wind: ${r.wind.speed}km/h`,a.innerText=`Humidity: ${r.main.humidity}%`,console.log(r)}catch(e){console.error(e)}}))})();